function copyToClipboard(e){const t=e.closest(".code-block"),n=t.querySelector("code").innerText;navigator.clipboard.writeText(n).then(()=>{const t=e.innerHTML;e.innerHTML="Copied!",e.disabled=!0,setTimeout(()=>{e.innerHTML=t,e.disabled=!1},2e3)}).catch(e=>{console.error("Failed to copy text: ",e)})}document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelectorAll(".copy-button");e.forEach(e=>{e.addEventListener("click",()=>copyToClipboard(e))})})